import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { FontLoader } from 'three/examples/jsm/loaders/FontLoader';
import { TextGeometry } from 'three/examples/jsm/geometries/TextGeometry';

const ThreeScene = () => {
  const canvasRef = useRef(null); // Reference to the canvas element

  useEffect(() => {
    let scene, camera, renderer;
    let textGeometry, textMesh;
    let font;

    // Initialize Three.js scene
    const initScene = () => {
      scene = new THREE.Scene();
      camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
      renderer = new THREE.WebGLRenderer({ canvas: canvasRef.current });

      handleResize(); // Handle initial canvas size
      window.addEventListener('resize', handleResize);
    };

    // Load font and create text geometry
    const loadFontAndCreateText = () => {
      const fontLoader = new FontLoader();
      fontLoader.load('/Assets/Fonts/helvetiker_regular.typeface.json', loadedFont => {
        console.log(loadedFont)
        console.log("Font Loaded")
        createTextGeometry();
      });
    };

    // Create text geometry
    const createTextGeometry = () => {
      if (textMesh) {
        scene.remove(textMesh);
        textGeometry.dispose();
      }

      textGeometry = new TextGeometry('Hello Krish', {
        font: font,
        size: 0.5,
        depth: 0.2,
        curveSegments: 12,
        bevelEnabled: true,
        bevelThickness: 0.03,
        bevelSize: 0.02,
        bevelOffset: 0,
        bevelSegments: 5
      });

      textMesh = new THREE.Mesh(textGeometry, new THREE.MeshBasicMaterial({ color: 0xffffff }));
      scene.add(textMesh);
    };

    // Handle window resize
    const handleResize = () => {
      const { innerWidth, innerHeight } = window;
      camera.aspect = innerWidth / innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(innerWidth, innerHeight);
    };

    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    };

    // Initialize scene and start animation loop
    initScene();
    loadFontAndCreateText();
    animate();

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []); // Empty dependency array for initialization only

  return <canvas ref={canvasRef} />;
};

export default ThreeScene;
